import ChartBar from "./ChartBar";
import "./Chart.css";
const Chart = (props) => {
    const dataPointValues= props.dataPoints.map(datapoint => datapoint.value); // Transform object to number, returns array instead of obj
    const totalMaximum= Math.max(...dataPointValues) // Receives 12 arguments
  return (
    <div className="chart">
      {props.dataPoints.map((datapoint) => (
        <ChartBar
          key={datapoint.label}
          value={datapoint.value}
          maxValue={totalMaximum}
          label={datapoint.label}
        />
      ))}
    </div>
  );
};
export default Chart;
